using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.Networking;public class PenTrigger : NetworkBehaviour {    private void OnTriggerEnter(Collider other)    {        Debug.Log("Entered Pen");        Grabber grabber = other.gameObject.transform.GetComponent<Grabber>();        if(!grabber){            return;        }        Debug.Log("MAAAAAAN Entered Pen");        if (grabber.hasDog && isServer)        {            Debug.Log("MANANA HAS DOGGO");            SpawnController s = grabber.dog.GetComponent<SpawnController>();            if(s){                grabber.DropDog();                s.gameObject.GetComponent<DogController>().SetPenned(true);                Debug.Log("MANANA DROPPOD DOGGO");            }        }        //get man's grabber    }    // Use this for initialization    void Start () {			}		// Update is called once per frame	void Update () {			}}